from django import formsfrom .models import Addressclass AddressForm(forms.ModelForm):    address_line_1 = forms.CharField( widget=forms.TextInput(attrs={"class": 'form-control'}))    address_line_2 = forms.CharField(widget=forms.TextInput(attrs={"class": 'form-control'}), )    city = forms.CharField(widget=forms.TextInput(attrs={"class": 'form-control'}), )    country = forms.CharField(widget=forms.TextInput(attrs={"class": 'form-control'}), )    state = forms.CharField(widget=forms.TextInput(attrs={"class": 'form-control'}), )    postal_code = forms.CharField(widget=forms.TextInput(attrs={"class": 'form-control'}), )    class Meta:        model = Address        fields =[            # 'billing_profile',            # 'address_type',            'address_line_1',            'address_line_2',            'city',            'country',            'state',            'postal_code',        ]