from rest_framework import serializersfrom pizza.models import Pizza,Toppings,Sizes'''serializers --> JSONserializers --> validate data'''class ToppingSerializer(serializers.ModelSerializer):    class Meta:        model = Toppings        fields=[            'id',            'topping_name',        ]    # def get_topping_name(self, obj):    #     c_qs = Toppings.objects.filter_by_instance(obj)    #    #     return c_qs.all()class SizesSerializer(serializers.ModelSerializer):    class Meta:        model = Sizes        fields=[            'id',            'pizza_size',        ]class PizzaSerializer(serializers.ModelSerializer):    topping_type =   ToppingSerializer(many=True)    size_type = SizesSerializer()    class Meta:        model = Pizza        fields=[            'id',            'pizza_type',            'size_type' ,            'topping_type'        ]    # def get_topping_type(self, obj):    #     return obj.topping_type.all()