from django.urls import path,re_pathfrom .views import (                    home_page,                    add_pizza,                    add_topping,                    delete_pizza,                    edit_pizza                  )app_name='pizza'urlpatterns = [    re_path('^$',home_page,name='home'),    path('add_pizza/',add_pizza,name='add_pizza_view'),    path('add_topping/',add_topping,name='add_topping_view'),    re_path(r'^edit_pizza/(?P<id>[0-9]+)/$',edit_pizza,name='edit_pizza_view'),    re_path(r'^delete_topping/(?P<pizza_id>[0-9]+)/$', delete_pizza,name='delete_pizza_view'),]