from django.http import HttpResponsefrom django.shortcuts import renderimport operatordef homepage(request):    # return HttpResponse("<h1>This is Homepage</h1>")    return render(request,'home.html',{'name':'Hello my name is Akash Verma'})def count(request):    data=request.GET['fulltextarea']    word_list=data.split()    word_len=len(word_list)    word_dictionary={}    for word in word_list:        if word in word_dictionary:            word_dictionary[word]+=1        else:            word_dictionary[word]=1    sorted_dict=sorted(word_dictionary.items(),key=operator.itemgetter(1),reverse=True)    return render(request,'count.html',{'data':data,'words':word_len,'worddictionary':sorted_dict})def about(request):    return render(request,'about.html')